import createStore from 'unistore';
import axios from "axios"

const initialState = { 
                username : '',
                password : '',
                email : '',
                token : '',
                auth : false,
                listNews : [],
                isLoading : true,
                category : 'general',
                search : ''
                };

export const store = createStore(initialState);

export const actions = store => ({
    setInput : (state, event) => {
        console.warn('setinput masuk')
        console.warn(store.username)
        store.setState({ [event.target.name] : event.target.value })
    },

    afterSignin : (state) => {
        const { username, password } = state;
        const input = {
            username : username,
            password : password
        }
        axios
            .post("https://authaltab4.free.beeceptor.com/auth", input)
            .then(function(response) {
                console.warn(response.data)
                if(response.data.hasOwnProperty("token")) {
                    store.setState({ username : response.data.username })
                    store.setState({ email : response.data.email })
                    store.setState({ token : response.daresponse.data.tokenta.token })
                    store.setState({ auth : true })
                    this.props.history.push("/profile")
                }
            })
            .catch(function(error) {
                console.log(error)
            })
    }
})
