import createStore from 'unistore';
import axios from 'axios'


const initialState = { 
                apiKey : "58fecc904b4e40ef920ae5582000d89a",
                baseUrl : "https://newsapi.org/v2/",
                urlHeadline : baseUrl + "everything?language=en&apiKey=" + apiKey,
                username : '',
                password : '',
                email : '',
                token : '',
                auth : false,
                listNews : [],
                isLoading : true,
                category : 'general',
                search : ''
                };

export const store = createStore(initialState);

export const actions = store => ({
    setInput : (state, event) => {
        store.setState({ [event.target.name] : event.target.value })
    },

    getNewsAxios : (state, paramCategory) => {
        axios
            .get(store.urlHeadline+'&q='+paramCategory)
            .then(function(response) {
                store.setState({ listNews: response.data.articles, isLoading: false})
            })

            .catch(function(error) {
                store.setState({ isLoading: false })
            })
    },

    searchNews : async (state, keyword) => {
        if (keyword.length > 2) {
          try {
            const response = await axios.get(urlHeadline+'&q='+keyword);
            store.setState({ listNews: response.data.articles });
          } catch (error) {
            console.error(error);
          }
        }
    }

})
