import createStore from 'unistore';
import axios from "axios"

const initialState = { 
                username : '',
                password : '',
                email : '',
                token : '',
                auth : false,
                listNews : [],
                isLoading : true,
                category : 'general',
                search : ''
                };

export const store = createStore(initialState);

export const actions = store => ({
    setInput : (state, event) => {
        store.setState({ [event.target.name] : event.target.value })
    },

    afterSignin : async (state) => {
        const { username, password } = state;
        const input = {
            username : username,
            password : password
        }
        await axios
            .post("https://alta123.free.beeceptor.com/auth", input)
            .then(function(response) {
                console.warn(response.data)
                if(response.data.hasOwnProperty("token")) {
                    store.setState({ username : response.data.username })
                    store.setState({ email : response.data.email })
                    store.setState({ token : response.data.token })
                    store.setState({ auth : true })
                    console.warn('true')
                    return true
                }
            })
            .catch(function(error) {
                console.log(error)
                return false
            })
    }
})
