import React, { Component } from 'react';
import Header from "../components/header"

class Auth extends Component {
    state = {
        category : ''
    }

    setCategory = (value) => {
        const self = this;
        self.setState({category : value})
    }

    render () {
        return (
            <div>
                <Header setCategory={this.setCategory} />
            </div>    
        );
    }
}

export default Auth;
